import "./styles.scss";
import {useEffect, useState} from "react";
import {faker} from '@faker-js/faker';

// import CustomerTable from "./components/CustomerTable";

/**
 *
 * Welcome to the Data Sorting coding challenge.
 *
 * Load json data from `/public/data.json` and render it in a table
 * using `/public/table.png` design.
 *
 * Make this behaviour reusable.
 *
 * Ask questions & have fun!
 *
 */
function App() {

    const [data, getData] = useState([])


    useEffect(() => {
        fetchData()
    }, [])


    const fetchData = () => {
        fetch("data.json")
            .then((res) =>
                res.json())

            .then((response) => {
                console.log(response);
                getData(response);
            })

    }

    return (
        <div>

            <h1>Phoenix Technologies Code Challenge</h1>
            <table>
                <thead>
                <tr>
                    <th>User</th>
                    <th>Name</th>
                    <th>Joined On</th>
                    <th>Last Login</th>
                    <th>Transactions/Balance</th>
                    <th>Status</th>
                    <th>Bio</th>
                </tr>
                </thead>
                {data.map((item, i) => (
                    <tbody key={i}>
                    <tr>
                        {/*<td>*/}
                        {/*    /!*<img src={item.avatar}/>,*!/*/}
                        {/*    {item.userName},*/}
                        {/*    {item.email}*/}
                        {/*</td>*/}
                        {/*<td>{item.name}</td>*/}
                        {/*<td>{item.joinedAt}</td>*/}
                        {/*<td>{item.lastLogin}</td>*/}
                        {/*<td>{"-$" + faker.finance.amount()}, {"$500"}</td>*/}
                        {/*<td>{item.status}</td>*/}
                        {/*<td>{item.bio}</td> */}
                        <td>
                            {/*<img src={item.avatar}/>,*/}
                            {"Elias.Hand"},
                            {"Kailey.Krajcik71@gmail.com"}
                        </td>
                        <td>{"Aaron Auer"}</td>
                        <col>{"1997-01-31T02:27:08.418Z"}</col>
                        <td>{"2021-07-16T22:40:31.933Z"}</td>
                        <td>{"-$" + faker.finance.amount()}, {"$500"}</td>
                        <td>{"Inactive"}</td>
                        <td>{"Quia magni reprehenderit."}</td>
                    </tr>
                    </tbody>
                ))}

            </table>
        </div>
    );
}

export default App;
